mapper.insert:
INSERT INTO BOARD_CALL ( UNO, GNO, REPLY_DEPTH, CAT_GROUP, ID, SUBJECT, CONTENT, HIT, IN_DATE, ATT_FILE, EMPNM, NOCODE ) VALUES ( (SELECT NVL(MAX(UNO), 0) + 1 FROM BOARD_CALL WHERE CAT_GROUP = #{CAT_GROUP}), (SELECT NVL(MAX(GNO), 0) + 1 FROM BOARD_CALL WHERE CAT_GROUP = #{CAT_GROUP}), 'A', #{CAT_GROUP}, #{ID}, #{SUBJECT}, #{CONTENT}, 0, to_char(SYSDATE,'yyyy-MM-dd HH:mm:ss'),
#{ATT_FILE}
NULL , #{EMPNM}, '1' )

mapper.update:
UPDATE BOARD_CALL
SET SUBJECT = #{SUBJECT},
CONTENT = #{CONTENT},
    ATT_FILE = #{ATT_FILE},
EMPNM = #{EMPNM},
IN_DATE = to_char(SYSDATE,'yyyy-MM-dd HH:mm:ss')
WHERE UNO = #{UNO}
AND CAT_GROUP = #{CAT_GROUP}
AND ID = #{ID}

