mapper.select:
SELECT main_query.CUST_CODE as custCode, main_query.PROJECT_CODE as projectCode, ( SELECT PROJECT_NAME FROM TPRJ01 WHERE PROJECT_CODE = main_query.PROJECT_CODE AND CUST_CODE = #{params.custCode} ) as projectName, main_query.PERSON_CODE as personCode, main_query.CALL_CODE as callCode, NVL( ( SELECT PERSON_NAME FROM TPER01 WHERE PERSON_CODE = main_query.PERSON_CODE AND CUST_CODE = #{params.custCode} ), ' ' ) as empNo, ( SELECT TEL_NO FROM TPER01 WHERE PERSON_CODE = main_query.PERSON_CODE AND CUST_CODE = #{params.custCode} ) as custTell, main_query.INCALL_NO as incallNo, main_query.OUTCALL_NO as outcallNo, main_query.RING_TIME as ringTime, main_query.RING_ID as ringId, main_query.CHANNEL_TIME as channelTime, main_query.CHANNEL_ID as channelId, main_query.OUT_TIME as outTime, main_query.OUT_ID as outId, ( SELECT ADM_SNAME FROM TSYS01 WHERE ADM_GUBN = '4002' AND ADM_CODE = main_query.CS_TYPE ) as csType, CASE WHEN INSTR(main_query.CS_NOTE, CHR(30)) > 0 THEN CASE WHEN LENGTH(SUBSTR(main_query.CS_NOTE, 1, INSTR(main_query.CS_NOTE, CHR(30)) - 1)) > 30 THEN SUBSTR(SUBSTR(main_query.CS_NOTE, 1, INSTR(main_query.CS_NOTE, CHR(30)) - 1), 1, 30) || '...' ELSE SUBSTR(main_query.CS_NOTE, 1, INSTR(main_query.CS_NOTE, CHR(30)) - 1) END ELSE CASE WHEN LENGTH(main_query.CS_NOTE) > 30 THEN SUBSTR(main_query.CS_NOTE, 1, 30) || '...' ELSE main_query.CS_NOTE END END as csNote, main_query.CS_DATE as csDate, main_query.CS_BANK as csBank, main_query.CS_NAME as csName, main_query.CS_CASH as csCash, ( SELECT ADM_SNAME FROM TSYS01 WHERE ADM_GUBN = '4003' AND ADM_CODE = main_query.PRC_GUBN ) as prcGubn, main_query.PRC_NOTE as prcNote, main_query.EMG_GUBN as emgGubn, main_query.RECALL_EMP as recallEmp, main_query.RECALL_CUST as recallCust, main_query.SAUP_GUBN as saupGubn, main_query.DEL_GUBN as delGubn, TO_CHAR(main_query.IN_DATE, 'YYYYMMDD') as inDate, TO_CHAR(main_query.IN_DATE, 'HH24:MI:SS') as inTime, main_query.IN_EMPNO as inEmpno, main_query.UP_DATE as up_Date, main_query.UP_EMPNO as upEmpno, ( SELECT ADM_SNAME FROM TSYS01 WHERE ADM_GUBN = '5000' AND ADM_CODE = main_query.BUY_GUBN ) as buyGubn, main_query.NEW_CS_TYPE as newCsType, main_query.NEW_CS_SUB_TYPE as newCsSubType, main_query.BUY_TYPE as buyType, main_query.COMPLETION_CODE as completionCode, ( SELECT COUNT(*) FROM TBND01_MEMO WHERE CUST_CODE = main_query.CUST_CODE AND PROJECT_CODE = main_query.PROJECT_CODE AND PERSON_CODE = main_query.PERSON_CODE AND CALL_CODE = main_query.CALL_CODE ) as COUNTRE, main_query.rnum FROM ( SELECT TBND01.CUST_CODE, TBND01.PROJECT_CODE, TBND01.PERSON_CODE, TBND01.CALL_CODE, TBND01.INCALL_NO, TBND01.OUTCALL_NO, TBND01.RING_TIME, TBND01.RING_ID, TBND01.CHANNEL_TIME, TBND01.CHANNEL_ID, TBND01.OUT_TIME, TBND01.OUT_ID, TBND01.CS_TYPE, TBND01.CS_NOTE, TBND01.CS_DATE, TBND01.CS_BANK, TBND01.CS_NAME, TBND01.CS_CASH, TBND01.PRC_GUBN, TBND01.PRC_NOTE, TBND01.EMG_GUBN, TBND01.RECALL_EMP, TBND01.RECALL_CUST, TBND01.SAUP_GUBN, TBND01.DEL_GUBN, TBND01.IN_DATE, TBND01.IN_EMPNO, TBND01.UP_DATE, TBND01.UP_EMPNO, TBND01.BUY_GUBN, TBND01.NEW_CS_TYPE, TBND01.NEW_CS_SUB_TYPE, TBND01.BUY_TYPE, TBND01_CUST.COMPLETION_CODE, ROW_NUMBER() OVER ( ORDER BY CASE WHEN TBND01.EMG_GUBN = '1' THEN 0 ELSE 1 END, TBND01.IN_DATE DESC ) AS rnum FROM TBND01 LEFT JOIN TBND01_CUST ON TBND01.CUST_CODE = TBND01_CUST.CUST_CODE AND TBND01.PROJECT_CODE = TBND01_CUST.PROJECT_CODE AND TBND01.PERSON_CODE = TBND01_CUST.PERSON_CODE AND TBND01.CALL_CODE = TBND01_CUST.CALL_CODE WHERE TBND01.CUST_CODE = #{params.custCode} AND TBND01.PRC_GUBN NOT IN ('3','4') ) main_query WHERE (main_query.EMG_GUBN = '1' OR TO_CHAR(main_query.IN_DATE, 'YYYYMMDD') BETWEEN #{params.startDate} AND #{params.endDate})
AND main_query.PRC_GUBN = #{params.status}
AND main_query.CS_TYPE = #{params.type}
AND main_query.BUY_GUBN = #{params.mall}
AND main_query.COMPLETION_CODE = #{params.custStat}
AND (main_query.CS_NOTE LIKE '%' || #{params.keyword} || '%' OR main_query.PRC_NOTE LIKE '%' || #{params.keyword} || '%')
AND main_query.EMG_GUBN = '1'
AND main_query.CS_TYPE = '29'
AND main_query.EMG_GUBN = '0'
AND main_query.rnum BETWEEN #{params.offset} AND #{params.limit} ORDER BY CASE WHEN main_query.EMG_GUBN = '1' THEN 0 ELSE 1 END, main_query.IN_DATE DESC

SELECT COUNT(*) FROM ( SELECT TBND01.CUST_CODE, TBND01.PROJECT_CODE, TBND01.PERSON_CODE, TBND01.CALL_CODE, TBND01.EMG_GUBN, TBND01.IN_DATE, TBND01.CS_TYPE, TBND01.PRC_GUBN, TBND01.BUY_GUBN, TBND01.CS_NOTE, TBND01.PRC_NOTE, TBND01_CUST.COMPLETION_CODE FROM TBND01 LEFT JOIN TBND01_CUST ON TBND01.CUST_CODE = TBND01_CUST.CUST_CODE AND TBND01.PROJECT_CODE = TBND01_CUST.PROJECT_CODE AND TBND01.PERSON_CODE = TBND01_CUST.PERSON_CODE AND TBND01.CALL_CODE = TBND01_CUST.CALL_CODE WHERE TBND01.CUST_CODE = #{params.custCode} AND TBND01.PRC_GUBN NOT IN ('3','4') ) main_query WHERE (main_query.EMG_GUBN = '1' OR TO_CHAR(main_query.IN_DATE, 'YYYYMMDD') BETWEEN #{params.startDate} AND #{params.endDate})
AND main_query.PRC_GUBN = #{params.status}
AND main_query.CS_TYPE = #{params.type}
AND main_query.BUY_GUBN = #{params.mall}
AND main_query.COMPLETION_CODE = #{params.custStat}
AND (main_query.CS_NOTE LIKE '%' || #{params.keyword} || '%' OR main_query.PRC_NOTE LIKE '%' || #{params.keyword} || '%')
AND main_query.EMG_GUBN = '1'
AND main_query.CS_TYPE = '29'
AND main_query.EMG_GUBN = '0'

SELECT CUST_CODE as custCode, PROJECT_CODE as projectCode, (SELECT PROJECT_NAME FROM TPRJ01 WHERE PROJECT_CODE=main_query.PROJECT_CODE AND CUST_CODE = #{params.custCode}) as projectName, PERSON_CODE as personCode, CALL_CODE as callCode, NVL((SELECT PERSON_NAME FROM TPER01 WHERE PERSON_CODE = main_query.PERSON_CODE AND CUST_CODE = #{params.custCode}), ' ') as empNo, (SELECT TEL_NO FROM TPER01 WHERE PERSON_CODE=main_query.PERSON_CODE AND CUST_CODE = #{params.custCode} ) as custTell, INCALL_NO as incallNo, OUTCALL_NO as outcallNo, RING_TIME as ringTime, RING_ID as ringId, CHANNEL_TIME as channelTime, CHANNEL_ID as channelId, OUT_TIME as outTime, OUT_ID as outId, (SELECT ADM_SNAME FROM TSYS01 WHERE ADM_GUBN='4002'AND ADM_CODE=main_query.CS_TYPE) as csType, CS_NOTE as csNote, CS_DATE as csDate, CS_BANK as csBank, CS_NAME as csName, CS_CASH as csCash, (SELECT ADM_SNAME FROM TSYS01 WHERE ADM_GUBN='4003'AND ADM_CODE=main_query.PRC_GUBN) as prcGubn, PRC_NOTE as prcNote, EMG_GUBN as emgGubn, RECALL_EMP as recallEmp, RECALL_CUST as recallCust, SAUP_GUBN as saupGubn, DEL_GUBN as delGubn, TO_CHAR(IN_DATE,'YYYYMMDD') as inDate, TO_CHAR(IN_DATE, 'HH24:MI:SS')as inTime, IN_EMPNO as inEmpno, UP_DATE as up_Date, UP_EMPNO as upEmpno, (SELECT ADM_SNAME FROM TSYS01 WHERE ADM_GUBN='5000'AND ADM_CODE=main_query.BUY_GUBN) as buyGubn, NEW_CS_TYPE as newCsType, NEW_CS_SUB_TYPE as newCsSubType, BUY_TYPE as buyType, COMPLETION_CODE as completionCode, (SELECT COUNT(*) FROM TBND01_MEMO WHERE CUST_CODE =main_query.CUST_CODE AND PROJECT_CODE=main_query.PROJECT_CODE AND PERSON_CODE=main_query.PERSON_CODE AND CALL_CODE =main_query.CALL_CODE) COUNTRE, rnum FROM ( SELECT TBND01.*, ROW_NUMBER() OVER ( ORDER BY CASE WHEN EMG_GUBN = '1' THEN 0 ELSE 1 END, IN_DATE DESC ) AS rnum FROM ( SELECT TBND01.* FROM TBND01 WHERE CUST_CODE = #{params.custCode} AND EMG_GUBN = '1' AND PRC_GUBN NOT IN ('3','4')
AND PRC_GUBN = #{params.status}
AND CS_TYPE = #{params.type}
AND BUY_GUBN = #{params.mall}
AND COMPLETION_CODE = #{params.custStat}
AND TO_CHAR(IN_DATE, 'YYYYMMDD') BETWEEN #{params.startDate} AND #{params.endDate}
UNION ALL SELECT TBND01.* FROM TBND01 WHERE CUST_CODE = #{params.custCode}
AND EMG_GUBN <> '1'
AND PRC_GUBN = #{params.status}
AND CS_TYPE = #{params.type}
AND BUY_GUBN = #{params.mall}
AND COMPLETION_CODE = #{params.custStat}
AND TO_CHAR(IN_DATE, 'YYYYMMDD') BETWEEN #{params.startDate} AND #{params.endDate} ) TBND01 where 1=1
AND (CS_NOTE LIKE '%' || #{params.keyword} || '%' OR PRC_NOTE LIKE '%' || #{params.keyword} || '%') -- 하위 쿼리에 별칭 추가
AND EMG_GUBN = '1'
AND CS_TYPE = '29'
AND EMG_GUBN = '0'
) main_query

SELECT B.SERL_NO as serlNo,
       B.USER_ID as userId,
       B.CONTEXT as conText,
       B.SAUP_GUBN as saupGubn,
       B.DEL_GUBN as delGubn,
       B.CONFIRM_YN as confirmYn,
       TO_CHAR(B.IN_DATE,'YYYYMMDD HH24:MI:SS') as inDate
FROM TBND01_MEMO B
WHERE B.CUST_CODE = #{params.custCode}
  AND B.PROJECT_CODE = #{params.projectCode}
  AND B.PERSON_CODE = #{params.personCode}
  AND B.CALL_CODE = #{params.callCode}
ORDER BY B.SERL_NO

SELECT CS_NOTE as csNote,
       (SELECT ADM_SNAME FROM TSYS01 WHERE ADM_GUBN='4003'AND  ADM_CODE=A.PRC_GUBN) as prcGubn,
       PRC_NOTE as prcNote,
       TO_CHAR(A.IN_DATE,'YYYYMMDD') as inDate,
       TO_CHAR(A.IN_DATE, 'HH24:MI:SS')as inTime
FROM TBND01 A
WHERE CUST_CODE = #{params.custCode}
  AND PROJECT_CODE = #{params.projectCode}
  AND PERSON_CODE = #{params.personCode}
ORDER BY IN_DATE DESC
