mapper.select:
SELECT CUST_CODE as custCode, PROJECT_CODE as projectCode, (SELECT PROJECT_NAME FROM TPRJ01 WHERE PROJECT_CODE=main_query.PROJECT_CODE AND CUST_CODE = #{params.custCode}) as projectName, PERSON_CODE as personCode, CALL_CODE as callCode, NVL((SELECT PERSON_NAME FROM TPER01 WHERE PERSON_CODE = main_query.PERSON_CODE AND CUST_CODE = #{params.custCode}), ' ') as empNo, (SELECT TEL_NO FROM TPER01 WHERE PERSON_CODE=main_query.PERSON_CODE AND CUST_CODE = #{params.custCode} ) as custTell, INCALL_NO as incallNo, OUTCALL_NO as outcallNo, RING_TIME as ringTime, RING_ID as ringId, CHANNEL_TIME as channelTime, CHANNEL_ID as channelId, OUT_TIME as outTime, OUT_ID as outId, (SELECT ADM_SNAME FROM TSYS01 WHERE ADM_GUBN='4002'AND ADM_CODE=main_query.CS_TYPE) as csType, CASE WHEN INSTR(CS_NOTE, CHR(30)) > 0 THEN CASE WHEN LENGTH(SUBSTR(CS_NOTE, 1, INSTR(CS_NOTE, CHR(30)) - 1)) > 30 THEN SUBSTR(SUBSTR(CS_NOTE, 1, INSTR(CS_NOTE, CHR(30)) - 1), 1, 30) || '...' ELSE SUBSTR(CS_NOTE, 1, INSTR(CS_NOTE, CHR(30)) - 1) END ELSE CASE WHEN LENGTH(CS_NOTE) > 30 THEN SUBSTR(CS_NOTE, 1, 30) || '...' ELSE CS_NOTE END END as csNote, CS_DATE as csDate, CS_BANK as csBank, CS_NAME as csName, CS_CASH as csCash, (SELECT ADM_SNAME FROM TSYS01 WHERE ADM_GUBN='4003'AND ADM_CODE=main_query.PRC_GUBN) as prcGubn, PRC_NOTE as prcNote, EMG_GUBN as emgGubn, RECALL_EMP as recallEmp, RECALL_CUST as recallCust, SAUP_GUBN as saupGubn, DEL_GUBN as delGubn, TO_CHAR(IN_DATE,'YYYYMMDD') as inDate, TO_CHAR(IN_DATE, 'HH24:MI:SS')as inTime, IN_EMPNO as inEmpno, UP_DATE as up_Date, UP_EMPNO as upEmpno, (SELECT ADM_SNAME FROM TSYS01 WHERE ADM_GUBN='5000'AND ADM_CODE=main_query.BUY_GUBN) as buyGubn, NEW_CS_TYPE as newCsType, NEW_CS_SUB_TYPE as newCsSubType, BUY_TYPE as buyType, (SELECT COUNT(*) FROM TBND01_MEMO WHERE CUST_CODE =main_query.CUST_CODE AND PROJECT_CODE=main_query.PROJECT_CODE AND PERSON_CODE=main_query.PERSON_CODE AND CALL_CODE =main_query.CALL_CODE) COUNTRE, rnum FROM ( SELECT TBND01.*, ROW_NUMBER() OVER ( ORDER BY CASE WHEN EMG_GUBN = '1' THEN 0 ELSE 1 END, IN_DATE DESC ) AS rnum FROM ( SELECT TBND01.* FROM TBND01 WHERE CUST_CODE = #{params.custCode} AND EMG_GUBN = '1'
AND PRC_GUBN = #{params.status}
AND CS_TYPE = #{params.type}
AND BUY_GUBN = #{params.mall}
AND TO_CHAR(IN_DATE, 'YYYYMMDD') BETWEEN #{params.startDate} AND #{params.endDate}
UNION ALL SELECT TBND01.* FROM TBND01 WHERE CUST_CODE = #{params.custCode}
AND EMG_GUBN <> '1'
AND PRC_GUBN = #{params.status}
AND CS_TYPE = #{params.type}
AND BUY_GUBN = #{params.mall}
AND TO_CHAR(IN_DATE, 'YYYYMMDD') BETWEEN #{params.startDate} AND #{params.endDate} ) TBND01 where 1=1
AND (CS_NOTE LIKE '%' || #{params.keyword} || '%' OR PRC_NOTE LIKE '%' || #{params.keyword} || '%') -- 하위 쿼리에 별칭 추가
AND EMG_GUBN = '1'
AND CS_TYPE = '29'
AND EMG_GUBN = '0'
) main_query WHERE rnum BETWEEN #{params.offset} AND #{params.limit}

SELECT COUNT(*) FROM (
SELECT TBND01.*
FROM TBND01
WHERE CUST_CODE =  #{params.custCode}
AND EMG_GUBN = '1'
UNION ALL
SELECT TBND01.*
FROM TBND01
WHERE CUST_CODE =  #{params.custCode}

    AND EMG_GUBN <> '1'

AND TO_CHAR(IN_DATE, 'YYYYMMDD') BETWEEN #{params.startDate} AND #{params.endDate}

) A
where 1=1

    AND PRC_GUBN = #{params.status}


    AND CS_TYPE = #{params.type}


    AND BUY_GUBN = #{params.mall}


    AND TO_CHAR(IN_DATE, 'YYYYMMDD') BETWEEN #{params.startDate} AND #{params.endDate}


    AND (CS_NOTE LIKE '%' || #{params.keyword} || '%' OR PRC_NOTE LIKE '%' || #{params.keyword} || '%')


    AND EMG_GUBN = '1'


    AND CS_TYPE = '29'


    AND EMG_GUBN = '0'

SELECT CUST_CODE as custCode, PROJECT_CODE as projectCode, (SELECT PROJECT_NAME FROM TPRJ01 WHERE PROJECT_CODE=main_query.PROJECT_CODE AND CUST_CODE = #{params.custCode}) as projectName, PERSON_CODE as personCode, CALL_CODE as callCode, NVL((SELECT PERSON_NAME FROM TPER01 WHERE PERSON_CODE = main_query.PERSON_CODE AND CUST_CODE = #{params.custCode}), ' ') as empNo, (SELECT TEL_NO FROM TPER01 WHERE PERSON_CODE=main_query.PERSON_CODE AND CUST_CODE = #{params.custCode} ) as custTell, INCALL_NO as incallNo, OUTCALL_NO as outcallNo, RING_TIME as ringTime, RING_ID as ringId, CHANNEL_TIME as channelTime, CHANNEL_ID as channelId, OUT_TIME as outTime, OUT_ID as outId, (SELECT ADM_SNAME FROM TSYS01 WHERE ADM_GUBN='4002'AND ADM_CODE=main_query.CS_TYPE) as csType, CS_NOTE as csNote, CS_DATE as csDate, CS_BANK as csBank, CS_NAME as csName, CS_CASH as csCash, (SELECT ADM_SNAME FROM TSYS01 WHERE ADM_GUBN='4003'AND ADM_CODE=main_query.PRC_GUBN) as prcGubn, PRC_NOTE as prcNote, EMG_GUBN as emgGubn, RECALL_EMP as recallEmp, RECALL_CUST as recallCust, SAUP_GUBN as saupGubn, DEL_GUBN as delGubn, TO_CHAR(IN_DATE,'YYYYMMDD') as inDate, TO_CHAR(IN_DATE, 'HH24:MI:SS')as inTime, IN_EMPNO as inEmpno, UP_DATE as up_Date, UP_EMPNO as upEmpno, (SELECT ADM_SNAME FROM TSYS01 WHERE ADM_GUBN='5000'AND ADM_CODE=main_query.BUY_GUBN) as buyGubn, NEW_CS_TYPE as newCsType, NEW_CS_SUB_TYPE as newCsSubType, BUY_TYPE as buyType, (SELECT COUNT(*) FROM TBND01_MEMO WHERE CUST_CODE =main_query.CUST_CODE AND PROJECT_CODE=main_query.PROJECT_CODE AND PERSON_CODE=main_query.PERSON_CODE AND CALL_CODE =main_query.CALL_CODE) COUNTRE, rnum FROM ( SELECT TBND01.*, ROW_NUMBER() OVER ( ORDER BY CASE WHEN EMG_GUBN = '1' THEN 0 ELSE 1 END, IN_DATE DESC ) AS rnum FROM ( SELECT TBND01.* FROM TBND01 WHERE CUST_CODE = #{params.custCode} AND EMG_GUBN = '1'
AND PRC_GUBN = #{params.status}
AND CS_TYPE = #{params.type}
AND BUY_GUBN = #{params.mall}
AND TO_CHAR(IN_DATE, 'YYYYMMDD') BETWEEN #{params.startDate} AND #{params.endDate}
UNION ALL SELECT TBND01.* FROM TBND01 WHERE CUST_CODE = #{params.custCode}
AND EMG_GUBN <> '1'
AND PRC_GUBN = #{params.status}
AND CS_TYPE = #{params.type}
AND BUY_GUBN = #{params.mall}
AND TO_CHAR(IN_DATE, 'YYYYMMDD') BETWEEN #{params.startDate} AND #{params.endDate} ) TBND01 where 1=1
AND (CS_NOTE LIKE '%' || #{params.keyword} || '%' OR PRC_NOTE LIKE '%' || #{params.keyword} || '%') -- 하위 쿼리에 별칭 추가
AND EMG_GUBN = '1'
AND CS_TYPE = '29'
AND EMG_GUBN = '0'
) main_query

SELECT B.SERL_NO as serlNo,
       B.USER_ID as userId,
       B.CONTEXT as conText,
       B.SAUP_GUBN as saupGubn,
       B.DEL_GUBN as delGubn,
       B.CONFIRM_YN as confirmYn,
       TO_CHAR(B.IN_DATE,'YYYYMMDD HH24:MI:SS') as inDate
FROM TBND01_MEMO B
WHERE B.CUST_CODE = #{params.custCode}
  AND B.PROJECT_CODE = #{params.projectCode}
  AND B.PERSON_CODE = #{params.personCode}
  AND B.CALL_CODE = #{params.callCode}
ORDER BY B.SERL_NO

SELECT CS_NOTE as csNote,
       (SELECT ADM_SNAME FROM TSYS01 WHERE ADM_GUBN='4003'AND  ADM_CODE=A.PRC_GUBN) as prcGubn,
       PRC_NOTE as prcNote,
       TO_CHAR(A.IN_DATE,'YYYYMMDD') as inDate,
       TO_CHAR(A.IN_DATE, 'HH24:MI:SS')as inTime
FROM TBND01 A
WHERE CUST_CODE = #{params.custCode}
  AND PROJECT_CODE = #{params.projectCode}
  AND PERSON_CODE = #{params.personCode}
ORDER BY IN_DATE DESC
