mapper.select:
select nvl(todayMiss,0)todayMiss, nvl(todayCom,0)count_Com, nvl(todayEme,0) todayEme, nvl(todayMiss+todayCom,0)count_sum, NVL(ROUND((todayCom / NULLIF(todayMiss + todayCom, 0)) * 100, 2), 0) AS processing_rate from ( select
( SELECT count(*) FROM (SELECT MAX(CALLDATE)CALL_DATE ,CLID,CUST_CODE,PROJECT_CODE FROM CALL_LOG_D WHERE substr(calldate,0,10)= TO_CHAR(SYSDATE,'YYYY-MM-DD') and cust_code is not null GROUP BY SUBSTR(calldate,0,10) , CLID,CUST_CODE,PROJECT_CODE) A,CALL_LOG_D B WHERE A.CALL_DATE = B.CALLDATE AND substr(calldate,0,10) =TO_CHAR(SYSDATE,'YYYY-MM-DD')
AND A.CLID = B.CLID AND RESULT <>'ANSWER' --and b.context <> 'outbound' and a.cust_code is not null
AND a.CUST_CODE = #{custCode}
) todayMiss, ( SELECT count(A.CUST_CODE) FROM TBND01 A WHERE 1=1 AND PRC_GUBN = '2' AND TO_CHAR(IN_DATE, 'YYYYMMDD') = TO_CHAR(SYSDATE,'YYYYMMDD')
AND CUST_CODE = #{custCode}
)todayCom, ( SELECT count(A.CUST_CODE) FROM TBND01 A WHERE 1=1 AND A.PRC_GUBN = '2' AND EMG_GUBN = '1' AND TO_CHAR(IN_DATE, 'YYYYMMDD') = to_char(sysdate,'YYYYMMDD') )todayEme from dual )

SELECT NVL( TO_CHAR( CASE WHEN percentChange > 100 THEN percentChange - 100 ELSE percentChange END, 'FM999999990.00' ), '0' ) AS processing_rate, dailyPoint, dailyPointN FROM ( SELECT (today.dailyPointN - yesterday.dailyPointN) / NULLIF(yesterday.dailyPointN, 0) * 100 AS percentChange, today.dailyPoint as dailyPoint, today.dailyPointN as dailyPointN FROM ( SELECT NVL(TO_CHAR(SUM(point) * -1, '999,999,999,999'), '0') AS dailyPoint, NVL(SUM(point) * -1, 0) AS dailyPointN FROM tcnt01_point WHERE call_code IS NOT NULL AND ISSUE_DATE =to_char(sysdate,'YYYYMMDD') -- 오늘 날짜
AND CUST_CODE = #{custCode}
) today, ( SELECT NVL(TO_CHAR(SUM(point) * -1, '999,999,999,999'), '0') AS dailyPoint, NVL(SUM(point) * -1, 0) AS dailyPointN FROM tcnt01_point WHERE call_code IS NOT NULL AND ISSUE_DATE = to_char(sysdate-1,'YYYYMMDD') -- 어제 날짜; 날짜가 잘못 지정되었으니 적절히 조정해야 함
AND CUST_CODE = #{custCode}
) yesterday )

select ( SELECT count(*) FROM (SELECT MAX(CALLDATE)CALL_DATE ,CLID,CUST_CODE,PROJECT_CODE FROM CALL_LOG_D WHERE substr(calldate,0,10)= TO_CHAR(SYSDATE,'YYYY-MM-DD') and cust_code is not null GROUP BY SUBSTR(calldate,0,10) , CLID,CUST_CODE,PROJECT_CODE) A,CALL_LOG_D B WHERE A.CALL_DATE = B.CALLDATE AND substr(calldate,0,10) =TO_CHAR(SYSDATE-1,'YYYY-MM-DD') AND A.CLID = B.CLID AND RESULT <>'ANSWER' --and b.context <> 'outbound' and a.cust_code is not null
AND A.CUST_CODE = #{custCode}
) yesterdayMiss, ( SELECT count(A.CUST_CODE) FROM TBND01 A WHERE 1=1 AND PRC_GUBN = '2' AND TO_CHAR(IN_DATE, 'YYYYMMDD') = TO_CHAR(SYSDATE-1,'YYYYMMDD')
AND A.CUST_CODE = #{custCode}
)yesterdayCom, ( SELECT count(A.CUST_CODE) FROM TBND01 A WHERE 1=1 AND A.PRC_GUBN = '2' AND EMG_GUBN = '1' AND TO_CHAR(IN_DATE, 'YYYYMMDD') = to_char(sysdate-1,'YYYYMMDD')
AND A.CUST_CODE = #{custCode}
)yesterdayEme, (
SELECT count(*) FROM (SELECT MAX(CALLDATE)CALL_DATE ,CLID,CUST_CODE,PROJECT_CODE FROM CALL_LOG_D WHERE substr(calldate,0,10)= TO_CHAR(SYSDATE,'YYYY-MM-DD') and cust_code is not null GROUP BY SUBSTR(calldate,0,10) , CLID,CUST_CODE,PROJECT_CODE) A,CALL_LOG_D B WHERE A.CALL_DATE = B.CALLDATE AND substr(calldate,0,10) =TO_CHAR(SYSDATE,'YYYY-MM-DD') AND A.CLID = B.CLID AND RESULT <>'ANSWER' --and b.context <> 'outbound' and a.cust_code is not null
AND A.CUST_CODE = #{custCode}
) todayMiss, ( SELECT count(A.CUST_CODE) FROM TBND01 A WHERE 1=1 AND PRC_GUBN = '2' AND TO_CHAR(IN_DATE, 'YYYYMMDD') = TO_CHAR(SYSDATE,'YYYYMMDD')
AND A.CUST_CODE = #{custCode}
)todayCom, ( SELECT count(A.CUST_CODE) FROM TBND01 A WHERE 1=1 AND A.PRC_GUBN = '2' AND EMG_GUBN = '1' AND TO_CHAR(IN_DATE, 'YYYYMMDD') = to_char(sysdate,'YYYYMMDD')
AND A.CUST_CODE = #{custCode}
)todayEme from dual

SELECT PRC_DATE,GUBN,HOUR_09,HOUR_10,HOUR_11,HOUR_12,HOUR_13,HOUR_14,HOUR_15,HOUR_16,HOUR_17,HOUR_18,HOUR_09+HOUR_10+HOUR_11+HOUR_12+HOUR_13+HOUR_14+HOUR_15+HOUR_16+HOUR_17+HOUR_18+HOUR_19 as callSum FROM ( SELECT TO_CHAR(IN_DATE, 'YYYYMMDD') PRC_DATE,'1' GUBN , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '09', 1, 0)) HOUR_09 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '10', 1, 0)) HOUR_10 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '11', 1, 0)) HOUR_11 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '12', 1, 0)) HOUR_12 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '13', 1, 0)) HOUR_13 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '14', 1, 0)) HOUR_14 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '15', 1, 0)) HOUR_15 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '16', 1, 0)) HOUR_16 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '17', 1, 0)) HOUR_17 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '18', 1, 0)) HOUR_18 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '19', 1, 0)) HOUR_19 FROM TBND01 A WHERE 1=1 AND PRC_GUBN ='2' AND TO_CHAR(IN_DATE, 'YYYYMMDD') = to_char(sysdate,'YYYYMMDD')
AND A.CUST_CODE = #{custCode}
GROUP BY TO_CHAR(IN_DATE, 'YYYYMMDD') UNION ALL SELECT TO_CHAR(IN_DATE, 'YYYYMMDD') PRC_DATE,'3' GUBN , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '09', 1, 0)) HOUR_09 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '10', 1, 0)) HOUR_10 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '11', 1, 0)) HOUR_11 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '12', 1, 0)) HOUR_12 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '13', 1, 0)) HOUR_13 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '14', 1, 0)) HOUR_14 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '15', 1, 0)) HOUR_15 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '16', 1, 0)) HOUR_16 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '17', 1, 0)) HOUR_17 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '18', 1, 0)) HOUR_18 , SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '19', 1, 0)) HOUR_19 FROM TBND01 A WHERE 1=1 AND PRC_GUBN ='2' AND TO_CHAR(IN_DATE, 'YYYYMMDD') = to_char(sysdate-1,'YYYYMMDD')
AND A.CUST_CODE = #{custCode}
GROUP BY TO_CHAR(IN_DATE, 'YYYYMMDD') UNION ALL SELECT SUBSTR(REPLACE(CALLDATE,'-',''), 1,8) PRC_DATE ,'2' GUBN , SUM(DECODE(SUBSTR(CALLDATE, 12,2), '09', 1, 0)) HOUR_09 , SUM(DECODE(SUBSTR(CALLDATE, 12,2), '10', 1, 0)) HOUR_10 , SUM(DECODE(SUBSTR(CALLDATE, 12,2), '11', 1, 0)) HOUR_11 , SUM(DECODE(SUBSTR(CALLDATE, 12,2), '12', 1, 0)) HOUR_12 , SUM(DECODE(SUBSTR(CALLDATE, 12,2), '13', 1, 0)) HOUR_13 , SUM(DECODE(SUBSTR(CALLDATE, 12,2), '14', 1, 0)) HOUR_14 , SUM(DECODE(SUBSTR(CALLDATE, 12,2), '15', 1, 0)) HOUR_15 , SUM(DECODE(SUBSTR(CALLDATE, 12,2), '16', 1, 0)) HOUR_16 , SUM(DECODE(SUBSTR(CALLDATE, 12,2), '17', 1, 0)) HOUR_17 , SUM(DECODE(SUBSTR(CALLDATE, 12,2), '18', 1, 0)) HOUR_18 , SUM(DECODE(SUBSTR(CALLDATE, 12,2), '19', 1, 0)) HOUR_19 FROM (SELECT B.CALLDATE,B.CLID,B.PERSON_CODE,B.CUST_CODE,B.PROJECT_CODE,B.empno,B.INCALL_NO ,1 COUNT,B.RESULT,B.CONTEXT FROM (SELECT MAX(CALLDATE)CALL_DATE ,CLID,CUST_CODE,PROJECT_CODE FROM CALL_LOG_D WHERE substr(calldate,0,10)=to_char(sysdate,'YYYY-MM-DD') and cust_code is not null GROUP BY SUBSTR(calldate,0,10) , CLID,CUST_CODE,PROJECT_CODE) A,CALL_LOG_D B WHERE A.CALL_DATE = B.CALLDATE AND substr(calldate,0,10)=to_char(sysdate,'YYYY-MM-DD') AND A.CLID = B.CLID
AND RESULT <>'ANSWER' -- and b.context <> 'outbound'
AND A.CUST_CODE = #{custCode}
ORDER BY CALLDATE DESC) a WHERE 1=1 AND CUST_CODE IS NOT NULL GROUP BY SUBSTR(REPLACE(CALLDATE,'-',''), 1,8) ) ORDER BY PRC_DATE

SELECT IN_DATE_MONTH as PERSONMONTH, SUM(CASE WHEN DATE_MATCH = 'N' THEN 1 ELSE 0 END) AS NEWPERSONCOUNT, SUM(CASE WHEN DATE_MATCH = 'O' THEN 1 ELSE 0 END) AS OLDPERSONCOUNT FROM ( SELECT TO_CHAR(B.IN_DATE, 'YYYY-MM') AS IN_DATE_MONTH,A.PERSON_CODE, CASE WHEN TO_CHAR(A.IN_DATE, 'YYYYMM') = TO_CHAR(B.IN_DATE, 'YYYYMM') THEN 'N' ELSE 'O' END AS DATE_MATCH FROM TPER01 A JOIN TBND01 B ON A.CUST_CODE = B.CUST_CODE AND A.PROJECT_CODE = B.PROJECT_CODE AND A.PERSON_CODE = B.PERSON_CODE
AND A.CUST_CODE = #{custCode}
WHERE
B.IN_DATE >= ADD_MONTHS(TRUNC(SYSDATE, 'MM'), -11) AND B.IN_DATE < TRUNC(SYSDATE, 'MM') + INTERVAL '1' MONTH
) GROUP BY IN_DATE_MONTH ORDER BY IN_DATE_MONTH

SELECT A AS THISMONTH ,B PREVIOUSMONTH, CASE WHEN A >= B THEN TRUNC((A / B) * 100) ELSE TRUNC((A / B) * 100) END AS PERCENTCHANGE FROM ( SELECT ( SELECT COUNT(CUST_CODE) FROM TBND01 WHERE TO_CHAR(IN_dATE,'YYYYMM') = TO_CHAR(SYSDATE,'YYYYMM')
AND CUST_CODE = #{custCode}
AND PRC_GUBN = '2' ) AS A, ( SELECT COUNT(CUST_CODE) FROM TBND01 WHERE TO_CHAR(IN_DATE,'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1),'YYYYMM')
AND CUST_CODE = #{custCode}
AND PRC_GUBN = '2' ) AS B FROM DUAL )

WITH DatePeriods AS ( SELECT '월요일' AS weekday, 2 AS day_num FROM DUAL UNION ALL SELECT '화요일', 3 FROM DUAL UNION ALL SELECT '수요일', 4 FROM DUAL UNION ALL SELECT '목요일', 5 FROM DUAL UNION ALL SELECT '금요일', 6 FROM DUAL ), Periods AS ( SELECT '1W' AS period, TRUNC(SYSDATE-8) AS start_date, TRUNC(SYSDATE-1)-1 AS end_date, 1 AS weeks FROM DUAL UNION ALL SELECT '1M', ADD_MONTHS(TRUNC(SYSDATE, 'MM')-1, -1), TRUNC(SYSDATE, 'MM')-1-1, 4 AS weeks FROM DUAL UNION ALL SELECT '3M', ADD_MONTHS(TRUNC(SYSDATE, 'MM')-1, -3), TRUNC(SYSDATE, 'MM')-1-1, 13 AS weeks FROM DUAL UNION ALL SELECT '6M', ADD_MONTHS(TRUNC(SYSDATE, 'MM')-1, -6), TRUNC(SYSDATE, 'MM')-1-1, 26 AS weeks FROM DUAL UNION ALL SELECT '9M', ADD_MONTHS(TRUNC(SYSDATE, 'MM')-1, -9), TRUNC(SYSDATE, 'MM')-1-1, 26 AS weeks FROM DUAL UNION ALL SELECT '12M', ADD_MONTHS(TRUNC(SYSDATE, 'MM')-1, -12), TRUNC(SYSDATE, 'MM')-1-1, 52 AS weeks FROM DUAL ), AggregatedData AS ( SELECT dp.weekday, p.period, COUNT(*) AS count, p.weeks AS weeks FROM twowincall.tbnd01 t JOIN Periods p ON t.IN_DATE BETWEEN p.start_date AND p.end_date JOIN DatePeriods dp ON TO_CHAR(t.IN_DATE, 'D', 'NLS_DATE_LANGUAGE=AMERICAN') = TO_CHAR(dp.day_num) WHERE t.PRC_GUBN = '2'
AND CUST_CODE = #{custCode}
AND TO_CHAR(t.IN_DATE, 'D', 'NLS_DATE_LANGUAGE=AMERICAN') BETWEEN '2' AND '6' GROUP BY dp.weekday, p.period, p.weeks ) SELECT dp.weekday, ROUND(SUM(CASE WHEN ad.period = '1W' THEN ad.count ELSE 0 END) / MAX(CASE WHEN ad.period = '1W' THEN ad.weeks ELSE 1 END), 0) AS avg1week, ROUND(SUM(CASE WHEN ad.period = '1M' THEN ad.count ELSE 0 END) / MAX(CASE WHEN ad.period = '1M' THEN ad.weeks ELSE 4 END), 0) AS avg1month, ROUND(SUM(CASE WHEN ad.period = '3M' THEN ad.count ELSE 0 END) / MAX(CASE WHEN ad.period = '3M' THEN ad.weeks ELSE 13 END), 0) AS avg3months, ROUND(SUM(CASE WHEN ad.period = '6M' THEN ad.count ELSE 0 END) / MAX(CASE WHEN ad.period = '6M' THEN ad.weeks ELSE 26 END), 0) AS avg6months, ROUND(SUM(CASE WHEN ad.period = '9M' THEN ad.count ELSE 0 END) / MAX(CASE WHEN ad.period = '9M' THEN ad.weeks ELSE 26 END), 0) AS avg9months, ROUND(SUM(CASE WHEN ad.period = '12M' THEN ad.count ELSE 0 END) / MAX(CASE WHEN ad.period = '12M' THEN ad.weeks ELSE 52 END), 0) AS avg12months FROM DatePeriods dp LEFT JOIN AggregatedData ad ON dp.weekday = ad.weekday GROUP BY dp.weekday ORDER BY CASE dp.weekday WHEN '월요일' THEN 1 WHEN '화요일' THEN 2 WHEN '수요일' THEN 3 WHEN '목요일' THEN 4 WHEN '금요일' THEN 5 END
