select nvl(todayMiss,0) as todayMiss, nvl(todayCom,0) as todayCom, nvl(todayEme,0) as todayEme, nvl(todayMiss+todayCom,0) as count_sum, NVL(ROUND((todayCom / NULLIF(todayMiss + todayCom, 0)) * 100, 2), 0) AS PROCESSING_RATE from ( select
( SELECT count(*) FROM (SELECT MAX(CALLDATE)CALL_DATE ,CLID,CUST_CODE,PROJECT_CODE FROM CALL_LOG_D WHERE substr(calldate,0,10)= TO_CHAR(SYSDATE,'YYYY-MM-DD') and cust_code is not null GROUP BY SUBSTR(calldate,0,10) , CLID,CUST_CODE,PROJECT_CODE) A,CALL_LOG_D B WHERE A.CALL_DATE = B.CALLDATE AND substr(calldate,0,10) =TO_CHAR(SYSDATE,'YYYY-MM-DD')
AND A.CLID = B.CLID AND RESULT <>'ANSWER' --and b.context <> 'outbound' and a.cust_code is not null
AND a.CUST_CODE = #{custCode}
) todayMiss, ( SELECT count(A.CUST_CODE) FROM TBND01 A WHERE 1=1 AND PRC_GUBN = '2' AND TO_CHAR(IN_DATE, 'YYYYMMDD') = TO_CHAR(SYSDATE,'YYYYMMDD')
AND CUST_CODE = #{custCode}
)todayCom, ( SELECT count(A.CUST_CODE) FROM TBND01 A WHERE 1=1 AND A.PRC_GUBN = '2' AND EMG_GUBN = '1' AND TO_CHAR(IN_DATE, 'YYYYMMDD') = to_char(sysdate,'YYYYMMDD') )todayEme from dual )

select nvl(todayMiss,0) as todayMiss, nvl(todayCom,0) as todayCom, nvl(todayEme,0) as todayEme, nvl(todayMiss+todayCom,0) as count_sum, NVL(ROUND((todayCom / NULLIF(todayMiss + todayCom, 0)) * 100, 2), 0) AS PROCESSING_RATE from ( select
( SELECT count(*) FROM (SELECT MAX(CALLDATE)CALL_DATE ,CLID,CUST_CODE,PROJECT_CODE FROM CALL_LOG_D WHERE substr(calldate,0,10)= TO_CHAR(SYSDATE-1,'YYYY-MM-DD') and cust_code is not null GROUP BY SUBSTR(calldate,0,10) , CLID,CUST_CODE,PROJECT_CODE) A,CALL_LOG_D B WHERE A.CALL_DATE = B.CALLDATE AND substr(calldate,0,10) =TO_CHAR(SYSDATE-1,'YYYY-MM-DD')
AND A.CLID = B.CLID AND RESULT <>'ANSWER' --and b.context <> 'outbound' and a.cust_code is not null
AND a.CUST_CODE = #{custCode}
) todayMiss, ( SELECT count(A.CUST_CODE) FROM TBND01 A WHERE 1=1 AND PRC_GUBN = '2' AND TO_CHAR(IN_DATE, 'YYYYMMDD') = TO_CHAR(SYSDATE-1,'YYYYMMDD')
AND CUST_CODE = #{custCode}
)todayCom, ( SELECT count(A.CUST_CODE) FROM TBND01 A WHERE 1=1 AND A.PRC_GUBN = '2' AND EMG_GUBN = '1' AND TO_CHAR(IN_DATE, 'YYYYMMDD') = to_char(sysdate-1,'YYYYMMDD') )todayEme from dual )

select nvl(todayMiss,0) as todayMiss, nvl(todayCom,0) as todayCom, nvl(todayEme,0) as todayEme, nvl(todayMiss+todayCom,0) as count_sum, NVL(ROUND((todayCom / NULLIF(todayMiss + todayCom, 0)) * 100, 2), 0) AS PROCESSING_RATE from ( select
( SELECT count(*) FROM (SELECT MAX(CALLDATE)CALL_DATE ,CLID,CUST_CODE,PROJECT_CODE FROM CALL_LOG_D WHERE substr(calldate,0,10) BETWEEN TO_CHAR(SYSDATE-7,'YYYY-MM-DD') AND TO_CHAR(SYSDATE,'YYYY-MM-DD') and cust_code is not null GROUP BY SUBSTR(calldate,0,10) , CLID,CUST_CODE,PROJECT_CODE) A,CALL_LOG_D B WHERE A.CALL_DATE = B.CALLDATE AND substr(calldate,0,10) BETWEEN TO_CHAR(SYSDATE-7,'YYYY-MM-DD') AND TO_CHAR(SYSDATE,'YYYY-MM-DD')
AND A.CLID = B.CLID AND RESULT <>'ANSWER' --and b.context <> 'outbound' and a.cust_code is not null
AND a.CUST_CODE = #{custCode}
) todayMiss, ( SELECT count(A.CUST_CODE) FROM TBND01 A WHERE 1=1 AND PRC_GUBN = '2' AND TO_CHAR(IN_DATE, 'YYYYMMDD') BETWEEN TO_CHAR(SYSDATE-7,'YYYYMMDD') AND TO_CHAR(SYSDATE,'YYYYMMDD')
AND CUST_CODE = #{custCode}
)todayCom, ( SELECT count(A.CUST_CODE) FROM TBND01 A WHERE 1=1 AND A.PRC_GUBN = '2' AND EMG_GUBN = '1' AND TO_CHAR(IN_DATE, 'YYYYMMDD') BETWEEN TO_CHAR(SYSDATE-7,'YYYYMMDD') AND TO_CHAR(SYSDATE,'YYYYMMDD') )todayEme from dual )

select nvl(todayMiss,0) as todayMiss, nvl(todayCom,0) as todayCom, nvl(todayEme,0) as todayEme, nvl(todayMiss+todayCom,0) as count_sum, NVL(ROUND((todayCom / NULLIF(todayMiss + todayCom, 0)) * 100, 2), 0) AS PROCESSING_RATE from ( select
( SELECT count(*) FROM (SELECT MAX(CALLDATE)CALL_DATE ,CLID,CUST_CODE,PROJECT_CODE FROM CALL_LOG_D WHERE TO_CHAR(TO_DATE(substr(calldate,0,10), 'YYYY-MM-DD'), 'YYYYMM') = TO_CHAR(SYSDATE, 'YYYYMM') and cust_code is not null GROUP BY SUBSTR(calldate,0,10) , CLID,CUST_CODE,PROJECT_CODE) A,CALL_LOG_D B WHERE A.CALL_DATE = B.CALLDATE AND TO_CHAR(TO_DATE(substr(calldate,0,10), 'YYYY-MM-DD'), 'YYYYMM') = TO_CHAR(SYSDATE, 'YYYYMM')
AND A.CLID = B.CLID AND RESULT <>'ANSWER' --and b.context <> 'outbound' and a.cust_code is not null
AND a.CUST_CODE = #{custCode}
) todayMiss, ( SELECT count(A.CUST_CODE) FROM TBND01 A WHERE 1=1 AND PRC_GUBN = '2' AND TO_CHAR(IN_DATE, 'YYYYMM') = TO_CHAR(SYSDATE, 'YYYYMM')
AND CUST_CODE = #{custCode}
)todayCom, ( SELECT count(A.CUST_CODE) FROM TBND01 A WHERE 1=1 AND A.PRC_GUBN = '2' AND EMG_GUBN = '1' AND TO_CHAR(IN_DATE, 'YYYYMM') = TO_CHAR(SYSDATE, 'YYYYMM') )todayEme from dual )

SELECT NVL( TO_CHAR( CASE WHEN percentChange > 100 THEN percentChange - 100 ELSE percentChange END, 'FM999999990.00' ), '0' ) AS processing_rate, dailyPoint, dailyPointN FROM ( SELECT (today.dailyPointN - yesterday.dailyPointN) / NULLIF(yesterday.dailyPointN, 0) * 100 AS percentChange, today.dailyPoint as dailyPoint, today.dailyPointN as dailyPointN FROM ( SELECT NVL(TO_CHAR(SUM(point) * -1, '999,999,999,999'), '0') AS dailyPoint, NVL(SUM(point) * -1, 0) AS dailyPointN FROM tcnt01_point WHERE call_code IS NOT NULL AND ISSUE_DATE =to_char(sysdate,'YYYYMMDD') -- 오늘 날짜
AND CUST_CODE = #{custCode}
) today, ( SELECT NVL(TO_CHAR(SUM(point) * -1, '999,999,999,999'), '0') AS dailyPoint, NVL(SUM(point) * -1, 0) AS dailyPointN FROM tcnt01_point WHERE call_code IS NOT NULL AND ISSUE_DATE = to_char(sysdate-1,'YYYYMMDD') -- 어제 날짜; 날짜가 잘못 지정되었으니 적절히 조정해야 함
AND CUST_CODE = #{custCode}
) yesterday )

select ( SELECT count(*) FROM (SELECT MAX(CALLDATE)CALL_DATE ,CLID,CUST_CODE,PROJECT_CODE FROM CALL_LOG_D WHERE substr(calldate,0,10)= TO_CHAR(SYSDATE,'YYYY-MM-DD') and cust_code is not null GROUP BY SUBSTR(calldate,0,10) , CLID,CUST_CODE,PROJECT_CODE) A,CALL_LOG_D B WHERE A.CALL_DATE = B.CALLDATE AND substr(calldate,0,10) =TO_CHAR(SYSDATE-1,'YYYY-MM-DD') AND A.CLID = B.CLID AND RESULT <>'ANSWER' --and b.context <> 'outbound' and a.cust_code is not null
AND A.CUST_CODE = #{custCode}
) yesterdayMiss, ( SELECT count(A.CUST_CODE) FROM TBND01 A WHERE 1=1 AND PRC_GUBN = '2' AND TO_CHAR(IN_DATE, 'YYYYMMDD') = TO_CHAR(SYSDATE-1,'YYYYMMDD')
AND A.CUST_CODE = #{custCode}
)yesterdayCom, ( SELECT count(A.CUST_CODE) FROM TBND01 A WHERE 1=1 AND A.PRC_GUBN = '2' AND EMG_GUBN = '1' AND TO_CHAR(IN_DATE, 'YYYYMMDD') = to_char(sysdate-1,'YYYYMMDD')
AND A.CUST_CODE = #{custCode}
)yesterdayEme, (
SELECT count(*) FROM (SELECT MAX(CALLDATE)CALL_DATE ,CLID,CUST_CODE,PROJECT_CODE FROM CALL_LOG_D WHERE substr(calldate,0,10)= TO_CHAR(SYSDATE,'YYYY-MM-DD') and cust_code is not null GROUP BY SUBSTR(calldate,0,10) , CLID,CUST_CODE,PROJECT_CODE) A,CALL_LOG_D B WHERE A.CALL_DATE = B.CALLDATE AND substr(calldate,0,10) =TO_CHAR(SYSDATE,'YYYY-MM-DD') AND A.CLID = B.CLID AND RESULT <>'ANSWER' --and b.context <> 'outbound' and a.cust_code is not null
AND A.CUST_CODE = #{custCode}
) todayMiss, ( SELECT count(A.CUST_CODE) FROM TBND01 A WHERE 1=1 AND PRC_GUBN = '2' AND TO_CHAR(IN_DATE, 'YYYYMMDD') = TO_CHAR(SYSDATE,'YYYYMMDD')
AND A.CUST_CODE = #{custCode}
)todayCom, ( SELECT count(A.CUST_CODE) FROM TBND01 A WHERE 1=1 AND A.PRC_GUBN = '2' AND EMG_GUBN = '1' AND TO_CHAR(IN_DATE, 'YYYYMMDD') = to_char(sysdate,'YYYYMMDD')
AND A.CUST_CODE = #{custCode}
)todayEme from dual

SELECT PRC_DATE, GUBN, to_number(NVL(HOUR_09, 0)) as HOUR_09, to_number(NVL(HOUR_10, 0)) as HOUR_10, to_number(NVL(HOUR_11, 0)) as HOUR_11, to_number(NVL(HOUR_12, 0)) as HOUR_12, to_number(NVL(HOUR_13, 0)) as HOUR_13, to_number(NVL(HOUR_14, 0)) as HOUR_14, to_number(NVL(HOUR_15, 0)) as HOUR_15, to_number(NVL(HOUR_16, 0)) as HOUR_16, to_number(NVL(HOUR_17, 0)) as HOUR_17, to_number(NVL(HOUR_18, 0)) as HOUR_18, to_number(NVL(HOUR_19, 0)) as HOUR_19, to_number(NVL((HOUR_09+HOUR_10+HOUR_11+HOUR_12+HOUR_13+HOUR_14+HOUR_15+HOUR_16+HOUR_17+HOUR_18+HOUR_19), 0)) as CALLSUM FROM ( SELECT TO_CHAR(SYSDATE, 'YYYYMMDD') PRC_DATE, '1' GUBN, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '09', 1, 0)), 0) HOUR_09, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '10', 1, 0)), 0) HOUR_10, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '11', 1, 0)), 0) HOUR_11, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '12', 1, 0)), 0) HOUR_12, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '13', 1, 0)), 0) HOUR_13, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '14', 1, 0)), 0) HOUR_14, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '15', 1, 0)), 0) HOUR_15, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '16', 1, 0)), 0) HOUR_16, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '17', 1, 0)), 0) HOUR_17, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '18', 1, 0)), 0) HOUR_18, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '19', 1, 0)), 0) HOUR_19 FROM TBND01 WHERE PRC_GUBN = '2' AND TO_CHAR(IN_DATE, 'YYYYMMDD') = TO_CHAR(SYSDATE, 'YYYYMMDD')
AND CUST_CODE = #{custCode}
UNION ALL
SELECT TO_CHAR(SYSDATE, 'YYYYMMDD') PRC_DATE, '2' GUBN, nvl(SUM(DECODE(SUBSTR(CALLDATE, 12, 2), '09', 1, 0)), 0) HOUR_09, nvl(SUM(DECODE(SUBSTR(CALLDATE, 12, 2), '10', 1, 0)), 0) HOUR_10, nvl(SUM(DECODE(SUBSTR(CALLDATE, 12, 2), '11', 1, 0)), 0) HOUR_11, nvl(SUM(DECODE(SUBSTR(CALLDATE, 12, 2), '12', 1, 0)), 0) HOUR_12, nvl(SUM(DECODE(SUBSTR(CALLDATE, 12, 2), '13', 1, 0)), 0) HOUR_13, nvl(SUM(DECODE(SUBSTR(CALLDATE, 12, 2), '14', 1, 0)), 0) HOUR_14, nvl(SUM(DECODE(SUBSTR(CALLDATE, 12, 2), '15', 1, 0)), 0) HOUR_15, nvl(SUM(DECODE(SUBSTR(CALLDATE, 12, 2), '16', 1, 0)), 0) HOUR_16, nvl(SUM(DECODE(SUBSTR(CALLDATE, 12, 2), '17', 1, 0)), 0) HOUR_17, nvl(SUM(DECODE(SUBSTR(CALLDATE, 12, 2), '18', 1, 0)), 0) HOUR_18, nvl(SUM(DECODE(SUBSTR(CALLDATE, 12, 2), '19', 1, 0)), 0) HOUR_19 FROM ( SELECT B.CALLDATE, B.CLID, B.PERSON_CODE, B.CUST_CODE, B.PROJECT_CODE, B.empno, B.INCALL_NO, 1 COUNT, B.RESULT, B.CONTEXT FROM (SELECT MAX(CALLDATE) CALL_DATE, CLID, CUST_CODE, PROJECT_CODE FROM CALL_LOG_D WHERE SUBSTR(calldate, 0, 10) = TO_CHAR(SYSDATE, 'YYYY-MM-DD') AND CUST_CODE IS NOT NULL GROUP BY SUBSTR(calldate, 0, 10), CLID, CUST_CODE, PROJECT_CODE) A JOIN CALL_LOG_D B ON A.CALL_DATE = B.CALLDATE AND A.CLID = B.CLID WHERE SUBSTR(B.calldate, 0, 10) = TO_CHAR(SYSDATE, 'YYYY-MM-DD')
AND B.RESULT <> 'ANSWER' -- AND B.CONTEXT <> 'outbound'
AND A.CUST_CODE = #{custCode}
ORDER BY B.CALLDATE DESC ) a WHERE 1=1 AND CUST_CODE IS NOT NULL GROUP BY SUBSTR(REPLACE(CALLDATE, '-', ''), 1, 8)
UNION ALL
SELECT TO_CHAR(SYSDATE-1, 'YYYYMMDD') PRC_DATE, '3' GUBN, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '09', 1, 0)), 0) HOUR_09, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '10', 1, 0)), 0) HOUR_10, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '11', 1, 0)), 0) HOUR_11, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '12', 1, 0)), 0) HOUR_12, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '13', 1, 0)), 0) HOUR_13, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '14', 1, 0)), 0) HOUR_14, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '15', 1, 0)), 0) HOUR_15, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '16', 1, 0)), 0) HOUR_16, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '17', 1, 0)), 0) HOUR_17, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '18', 1, 0)), 0) HOUR_18, nvl(SUM(DECODE(TO_CHAR(IN_DATE, 'HH24'), '19', 1, 0)), 0) HOUR_19 FROM TBND01 WHERE PRC_GUBN = '2' AND TO_CHAR(IN_DATE, 'YYYYMMDD') = TO_CHAR(SYSDATE-1, 'YYYYMMDD')
AND CUST_CODE = #{custCode}
) ORDER BY PRC_DATE

SELECT IN_DATE_MONTH as PERSONMONTH, SUM(CASE WHEN DATE_MATCH = 'N' THEN 1 ELSE 0 END) AS NEWPERSONCOUNT, SUM(CASE WHEN DATE_MATCH = 'O' THEN 1 ELSE 0 END) AS OLDPERSONCOUNT FROM ( SELECT TO_CHAR(B.IN_DATE, 'YYYY-MM') AS IN_DATE_MONTH,A.PERSON_CODE, CASE WHEN TO_CHAR(A.IN_DATE, 'YYYYMM') = TO_CHAR(B.IN_DATE, 'YYYYMM') THEN 'N' ELSE 'O' END AS DATE_MATCH FROM TPER01 A JOIN TBND01 B ON A.CUST_CODE = B.CUST_CODE AND A.PROJECT_CODE = B.PROJECT_CODE AND A.PERSON_CODE = B.PERSON_CODE and B.PRC_GUBN='2'
AND A.CUST_CODE = #{custCode}
WHERE
B.IN_DATE >= ADD_MONTHS(TRUNC(SYSDATE, 'MM'), -11) AND B.IN_DATE < TRUNC(SYSDATE, 'MM') + INTERVAL '1' MONTH
) GROUP BY IN_DATE_MONTH ORDER BY IN_DATE_MONTH

SELECT A AS THISMONTH, B AS PREVIOUSMONTH, CASE WHEN B = 0 THEN 0 -- Avoid division by zero by directly returning 0 when B is zero. ELSE NVL(TRUNC((A / B) * 100), 0) END AS PERCENTCHANGE FROM ( SELECT ( SELECT NVL(COUNT(CUST_CODE), 0) FROM TBND01 WHERE TO_CHAR(IN_dATE, 'YYYYMM') = TO_CHAR(SYSDATE, 'YYYYMM')
AND CUST_CODE = #{custCode}
AND PRC_GUBN = '2' ) AS A, ( SELECT NVL(COUNT(CUST_CODE), 0) FROM TBND01 WHERE TO_CHAR(IN_DATE, 'YYYYMM') = TO_CHAR(ADD_MONTHS(SYSDATE, -1), 'YYYYMM')
AND CUST_CODE = #{custCode}
AND PRC_GUBN = '2' ) AS B FROM DUAL )

WITH DatePeriods AS ( SELECT '월요일' AS weekday, 2 AS day_num FROM DUAL UNION ALL SELECT '화요일', 3 FROM DUAL UNION ALL SELECT '수요일', 4 FROM DUAL UNION ALL SELECT '목요일', 5 FROM DUAL UNION ALL SELECT '금요일', 6 FROM DUAL ), Periods AS ( SELECT '1W' AS period, TRUNC(SYSDATE-8) AS start_date, TRUNC(SYSDATE)-1 AS end_date, 1 AS weeks FROM DUAL UNION ALL SELECT '1M', ADD_MONTHS(TRUNC(SYSDATE, 'MM'), -1), TRUNC(SYSDATE, 'MM'), 4 AS weeks FROM DUAL UNION ALL SELECT '3M', ADD_MONTHS(TRUNC(SYSDATE, 'MM'), -3), TRUNC(SYSDATE, 'MM'), 13 AS weeks FROM DUAL UNION ALL SELECT '6M', ADD_MONTHS(TRUNC(SYSDATE, 'MM'), -6), TRUNC(SYSDATE, 'MM'), 26 AS weeks FROM DUAL UNION ALL SELECT '9M', ADD_MONTHS(TRUNC(SYSDATE, 'MM'), -9), TRUNC(SYSDATE, 'MM'), 26 AS weeks FROM DUAL UNION ALL SELECT '12M', ADD_MONTHS(TRUNC(SYSDATE, 'MM'), -12), TRUNC(SYSDATE, 'MM'), 52 AS weeks FROM DUAL ), AggregatedData AS ( SELECT dp.weekday, p.period, COUNT(*) AS count, p.weeks AS weeks FROM twowincall.tbnd01 t JOIN Periods p ON t.IN_DATE BETWEEN p.start_date AND p.end_date JOIN DatePeriods dp ON TO_CHAR(t.IN_DATE, 'D', 'NLS_DATE_LANGUAGE=AMERICAN') = TO_CHAR(dp.day_num) WHERE t.PRC_GUBN = '2'
AND CUST_CODE = #{custCode}
AND TO_CHAR(t.IN_DATE, 'D', 'NLS_DATE_LANGUAGE=AMERICAN') BETWEEN '2' AND '6' GROUP BY dp.weekday, p.period, p.weeks ) SELECT dp.weekday, ROUND(SUM(CASE WHEN ad.period = '1W' THEN ad.count ELSE 0 END) / MAX(CASE WHEN ad.period = '1W' THEN ad.weeks ELSE 1 END), 0) AS avg1week, ROUND(SUM(CASE WHEN ad.period = '1M' THEN ad.count ELSE 0 END) / MAX(CASE WHEN ad.period = '1M' THEN ad.weeks ELSE 4 END), 0) AS avg1month, ROUND(SUM(CASE WHEN ad.period = '3M' THEN ad.count ELSE 0 END) / MAX(CASE WHEN ad.period = '3M' THEN ad.weeks ELSE 13 END), 0) AS avg3months, ROUND(SUM(CASE WHEN ad.period = '6M' THEN ad.count ELSE 0 END) / MAX(CASE WHEN ad.period = '6M' THEN ad.weeks ELSE 26 END), 0) AS avg6months, ROUND(SUM(CASE WHEN ad.period = '9M' THEN ad.count ELSE 0 END) / MAX(CASE WHEN ad.period = '9M' THEN ad.weeks ELSE 26 END), 0) AS avg9months, ROUND(SUM(CASE WHEN ad.period = '12M' THEN ad.count ELSE 0 END) / MAX(CASE WHEN ad.period = '12M' THEN ad.weeks ELSE 52 END), 0) AS avg12months FROM DatePeriods dp LEFT JOIN AggregatedData ad ON dp.weekday = ad.weekday GROUP BY dp.weekday ORDER BY CASE dp.weekday WHEN '월요일' THEN 1 WHEN '화요일' THEN 2 WHEN '수요일' THEN 3 WHEN '목요일' THEN 4 WHEN '금요일' THEN 5 END

SELECT WEEK, SUM(MONDAY) AS "MONDAY", SUM(TUESDAY) AS "TUESDAY", SUM(WEDNESDAY) AS "WEDNESDAY", SUM(THURSDAY) AS "THURSDAY", SUM(FRIDAY) AS "FRIDAY" FROM ( SELECT 'PRI' AS WEEK, COUNT(CASE WHEN TO_CHAR(IN_DATE,'DY', 'NLS_DATE_LANGUAGE=AMERICAN') = 'MON' THEN 1 END) AS MONDAY, COUNT(CASE WHEN TO_CHAR(IN_DATE,'DY', 'NLS_DATE_LANGUAGE=AMERICAN') = 'TUE' THEN 1 END) AS TUESDAY, COUNT(CASE WHEN TO_CHAR(IN_DATE,'DY', 'NLS_DATE_LANGUAGE=AMERICAN') = 'WED' THEN 1 END) AS WEDNESDAY, COUNT(CASE WHEN TO_CHAR(IN_DATE,'DY', 'NLS_DATE_LANGUAGE=AMERICAN') = 'THU' THEN 1 END) AS THURSDAY, COUNT(CASE WHEN TO_CHAR(IN_DATE,'DY', 'NLS_DATE_LANGUAGE=AMERICAN') = 'FRI' THEN 1 END) AS FRIDAY FROM TBND01 WHERE PRC_GUBN = '2'
AND CUST_CODE = #{custCode}
AND IN_DATE BETWEEN TRUNC(SYSDATE, 'IW') - 10 AND TRUNC(SYSDATE, 'IW') - 3 UNION ALL SELECT 'THI' AS WEEK, COUNT(CASE WHEN TO_CHAR(IN_DATE,'DY', 'NLS_DATE_LANGUAGE=AMERICAN') = 'MON' THEN 1 END) AS MONDAY, COUNT(CASE WHEN TO_CHAR(IN_DATE,'DY', 'NLS_DATE_LANGUAGE=AMERICAN') = 'TUE' THEN 1 END) AS TUESDAY, COUNT(CASE WHEN TO_CHAR(IN_DATE,'DY', 'NLS_DATE_LANGUAGE=AMERICAN') = 'WED' THEN 1 END) AS WEDNESDAY, COUNT(CASE WHEN TO_CHAR(IN_DATE,'DY', 'NLS_DATE_LANGUAGE=AMERICAN') = 'THU' THEN 1 END) AS THURSDAY, COUNT(CASE WHEN TO_CHAR(IN_DATE,'DY', 'NLS_DATE_LANGUAGE=AMERICAN') = 'FRI' THEN 1 END) AS FRIDAY FROM TBND01 WHERE PRC_GUBN = '2'
AND CUST_CODE = #{custCode}
AND IN_DATE BETWEEN TRUNC(SYSDATE, 'IW') AND TRUNC(SYSDATE, 'IW') + 5 ) GROUP BY WEEK ORDER BY WEEK DESC

WITH DateInfo AS ( SELECT TO_CHAR(SYSDATE, 'YYYYMM') AS CurrentMonth, TO_CHAR(SYSDATE, 'YYYYMMDD') AS CurrentDay, TRUNC(SYSDATE, 'IW') AS ThisWeekStart, TRUNC(SYSDATE, 'IW') + 5 AS ThisWeekEnd FROM DUAL ), AggregatedPoints AS ( SELECT TP.CUST_CODE,
SUM(CASE WHEN TP.CALL_CODE IS NULL AND TP.PERSON_CODE IS NULL AND TP.POINT_CHARGE IS NOT NULL THEN TP.POINT ELSE 0 END) AS POINT_CHARGE, SUM(CASE WHEN TP.POINT<0 THEN TP.POINT ELSE 0 END) AS POINT_USE, SUM(CASE WHEN TP.CALL_CODE IS NOT NULL AND TP.PERSON_CODE IS NOT NULL AND TP.POINT_CHARGE IS NULL AND TO_CHAR(TP.IN_DATE, 'YYYYMMDD') = CurrentDay THEN TP.POINT ELSE 0 END) AS POINT_USE_DAY, SUM(CASE WHEN TP.CALL_CODE IS NOT NULL AND TP.PERSON_CODE IS NOT NULL AND TP.POINT_CHARGE IS NULL AND TP.IN_DATE >= ThisWeekStart AND TP.IN_DATE < ThisWeekEnd THEN TP.POINT ELSE 0 END) AS POINT_USE_WEEK
FROM TCNT01_POINT TP INNER JOIN TCNT01 T ON TP.CUST_CODE = T.CUST_CODE CROSS JOIN DateInfo WHERE T.USE_YN = '1'
AND t.CUST_CODE = #{custCode}
AND ( TO_CHAR(TP.IN_DATE, 'YYYYMM') = CurrentMonth OR TP.IN_DATE >= ThisWeekStart )
GROUP BY TP.CUST_CODE ), TotalPoints AS ( SELECT SUM(POINT_CHARGE) AS DPOINTCHARGE, SUM(POINT_USE)*-1 AS DPOINTUSE, SUM(POINT_USE_DAY)*-1 AS DPOINTUSEDAY, SUM(POINT_USE_WEEK)*-1 AS DPOINTUSEWEEK FROM AggregatedPoints ) SELECT (SELECT to_char(nvl(SUM(T.POINT),0),'999,999,999') FROM TCNT01_POINT T INNER JOIN TCNT01 T1 ON T.CUST_CODE = T1.CUST_CODE WHERE T1.USE_YN = '1'
AND t.CUST_CODE = #{custCode}
) AS DPOINTSUM, to_char(nvl(TP.DPOINTCHARGE,0),'999,999,999') as DPOINTCHARGE , to_char(nvl(TP.DPOINTUSE,0),'999,999,999')as DPOINTUSE, to_char(nvl(TP.DPOINTUSEDAY,0),'999,999,999') as DPOINTUSEDAY, to_char(nvl(TP.DPOINTUSEWEEK,0),'999,999,999') as DPOINTUSEWEEK FROM TotalPoints TP

SELECT CUST_GRADE 
FROM TCNT01 
WHERE CUST_CODE = #{custCode}

SELECT authority 
FROM N_TCNT01_EMP 
WHERE USERID = #{userId} OR ID = #{userId}
